'use strict';

var websocket = require('./lib/websocket.js');
var stream = require('./lib/stream.js');
var websocketServer = require('./lib/websocket-server.js');
var receiver = require('./lib/receiver.js');
var sender = require('./lib/sender.js');

const WebSocket = websocket;

WebSocket.createWebSocketStream = stream;
WebSocket.Server = websocketServer;
WebSocket.Receiver = receiver;
WebSocket.Sender = sender;

var ws = WebSocket;

module.exports = ws;
